SELECT `reservation_id`,`reservation_name`,`status`,`status_name`,`reservation_date`, MIN(`start`) as `start`, MAX(`end`) as `end`, MAX(`people`) as `people`,SUM(`gross`) as `gross`, SUM(`net`) as `net` FROM `view_daily_subtotal` WHERE `reservation_date` BETWEEN '2025-05-01' AND '2025-09-30' AND `status` IN (1,2) GROUP BY `reservation_id` ORDER BY `reservation_date`,`reservation_id`;